@import url(~font-awesome/css/font-awesome.css);
@import url(~jquery-ui/themes/base/tooltip.css);
/* http://meyerweb.com/eric/tools/css/reset/
   v2.0 | 20110126
   License: none (public domain)
*/
html, body, div, span, applet, object, iframe,
h1, h2, h3, h4, h5, h6, p, blockquote, pre,
a, abbr, acronym, address, big, cite, code,
del, dfn, em, img, ins, kbd, q, s, samp,
small, strike, strong, sub, sup, tt, var,
b, u, i, center,
dl, dt, dd, ol, ul, li,
fieldset, form, label, legend,
table, caption, tbody, tfoot, thead, tr, th, td,
article, aside, canvas, details, embed,
figure, figcaption, footer, header, hgroup,
menu, nav, output, ruby, section, summary,
time, mark, audio, video {
  margin: 0;
  padding: 0;
  border: 0;
  font-size: 100%;
  font: inherit;
  vertical-align: baseline;
}

/* HTML5 display-role reset for older browsers */
article, aside, details, figcaption, figure,
footer, header, hgroup, menu, nav, section, main {
  display: block;
}

body {
  line-height: 1;
}

ol, ul {
  list-style: none;
}

blockquote, q {
  quotes: none;
}

blockquote:before, blockquote:after,
q:before, q:after {
  content: '';
  content: none;
}

table {
  border-collapse: collapse;
  border-spacing: 0;
}

/* borders vallen hiermee binnen afmetingen van een element:
*/
*, *::after, *::before {
  -webkit-box-sizing: border-box;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
}

.bb-block {
  padding: 8px 12px;
  margin: 10px 0;
  background-color: #f5f5f5;
  box-shadow: 0 0 10px #e8e8e8;
  transition: box-shadow .4s ease-out;
}

.bb-block:hover {
  box-shadow: 0 0 10px #f5f5f5;
}

.bb-block h3 {
  margin: 0.5em 0 1em 0;
}

.bb-block hr {
  background-color: #ccc;
  border-bottom: 1px solid #fff;
}

/* ketzerception */
.bb-block .bb-block {
  padding: 0;
  margin: 0;
  box-shadow: none;
}

.bb-block .bb-block .groep-tabs {
  visibility: hidden;
}

.bbcodePreview {
  display: none;
  margin: 7px 0;
  background-color: #ffffc1;
  border: 1px solid #ccc;
  padding: .2em .6em 10px;
  text-align: justify;
}

.bbcodePreview::after {
  content: "";
  display: block;
  clear: both;
}

.bbcodePreview, .bbcodePreview div, .Formulier .bbcodePreview {
  line-height: inherit;
}

.offtopic {
  font-size: .9em;
  line-height: 1em;
  color: #bbb;
}

.offtopic a {
  color: #bbb;
}

.offtopic hr {
  text-align: left;
  background-color: #bbb;
}

.offtopic img {
  opacity: 0.3;
  filter: alpha(opacity=30);
}

.bb-prive {
  background-color: #FFF0F5;
}

.bb-maaltijd {
  overflow: auto;
}

.bb-maaltijd .titel {
  font-size: 1.1em;
  font-weight: bold;
}

.bb-maaltijd {
  line-height: 2em;
}

.bb-maaltijd .aanmelddata {
  text-align: center;
  float: right;
  padding: 5px 15px 10px 15px;
  margin: 10px;
}

div.maaltijd-aangemeld {
  background-image: -webkit-linear-gradient(top, #5cb85c 0, #077b4e 100%);
  background-image: -o-linear-gradient(top, #5cb85c 0, #077b4e 100%);
  background-image: -webkit-gradient(linear, left top, left bottom, from(#5cb85c), to(#077b4e));
  background-image: linear-gradient(to bottom, #5cb85c 0, #077b4e 100%);
  filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#ff5cb85c', endColorstr='#ff077b4e', GradientType=0);
  background-repeat: repeat-x;
  color: white;
  font-weight: bold;
}

div.maaltijd-afgemeld {
  background-image: -webkit-linear-gradient(top, #d9534f 0, #7c2507 100%);
  background-image: -o-linear-gradient(top, #d9534f 0, #7c2507 100%);
  background-image: -webkit-gradient(linear, left top, left bottom, from(#d9534f), to(#7c2507));
  background-image: linear-gradient(to bottom, #d9534f 0, #7c2507 100%);
  filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#ffd9534f', endColorstr='#ff7c2507', GradientType=0);
  background-repeat: repeat-x;
  color: white;
  font-weight: bold;
}

a.maaltijd-aangemeld, span.maaltijd-aangemeld {
  font-weight: bold;
  color: green;
  background-color: #f5f5f5;
  border: 1px solid #a9a9a9;
  text-shadow: none;
  background-image: none;
}

a.maaltijd-afgemeld, span.maaltijd-afgemeld {
  font-weight: bold;
  color: red;
  background-color: #f5f5f5;
  border: 1px solid #a9a9a9;
  text-shadow: none;
  background-image: none;
}

.bb-gmap {
  width: 300px;
  height: 200px;
  border: 1px solid darkgrey;
  margin: 2px;
}

/* --------------------------------

Groepen

-------------------------------- */
.groep-samenvatting {
  padding-right: 2em;
  line-height: 1.5em;
