#login ul {
  list-style: none;
}

#menu, #login {
  display: flex;
  z-index: 10001;
  align-items: center;
  justify-content: center;
  pointer-events: none;
  user-select: none;
  -webkit-tap-highlight-color: transparent;
  background: rgba(46, 49, 65, 0.8);
  cursor: default;
  height: 100%;
  left: 0;
  opacity: 0;
  position: fixed;
  text-align: center;
  top: 0;
  visibility: hidden;
  width: 100%;
}

#menu .inner, #login .inner {
  padding: 1em;
  transform: translateY(0.5em);
  -webkit-overflow-scrolling: touch;
  background: #ce4e15;
  border-radius: 5px;
  display: block;
  max-width: 100%;
  position: relative;
  width: 18em;
  transition: transform 0.35s ease;
}

#menu h2, #login h2 {
  border-bottom: solid 2px rgba(255, 255, 255, 0.125);
  padding-bottom: 1em;
}

#menu .close, #login .close {
  background: url("../../images/close.svg") no-repeat;
  background-size: 4em 4em;
  border: 0;
  content: '';
  display: block;
  overflow: hidden;
  position: absolute;
  right: 0;
  text-align: center;
  text-indent: 4em;
  top: 0;
  width: 4em;
  height: 4em;
}

#menu .links, #login .links {
  list-style: none;
  padding: 0;
}

#menu .links li, #login .links li {
  padding: 0;
}

#menu .links li a, #login .links li a {
  border-radius: 5px;
  border: 0;
  display: block;
  font-family: Raleway, Helvetica, sans-serif;
  font-size: 0.8em;
  font-weight: 200;
  letter-spacing: 0.1em;
  line-height: 1.85em;
  padding: 0.75em 0;
  text-transform: uppercase;
}

#menu .links li a:hover, #login .links li a:hover {
  background: #414262;
}

#menu:target, #login:target {
  pointer-events: auto;
  opacity: 1;
  visibility: visible;
}

#menu:target ~ section, #login:target ~ section {
  filter: blur(1.5px);
}

#menu:target .overlay, #login:target .overlay {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
}

#menu:target .inner, #login:target .inner {
  transform: translateY(0);
  opacity: 1;
}

#login .Formulier {
  text-align: left;
}

@media screen and (max-width: 736px) {
  #menu .inner, #login .inner {
    max-height: 100%;
    overflow-y: auto;
    overflow-x: hidden;
  }
  #menu .inner .close, #login .inner .close {
    background-size: 1.5em 1.5em;
  }
}

.submenu {
  display: flex;
  justify-content: space-around;
  list-style: none;
  flex-flow: row wrap;
  font-family: Raleway, Helvetica, sans-serif;
  font-size: 0.8em;
  font-weight: 700;
  letter-spacing: 0.1em;
  line-height: 3em;
  text-transform: uppercase;
}

.submenu li {
