/*KNOPPEN*/
/*Knoppen in blauw gradient*/
.btn,
input[type=submit],
input[type=button],
.progress-bar-info,
.btn:focus,
input[type=submit]:focus,
input[type=button]:focus {
  background-image: linear-gradient(to bottom, #151b29, #2d7eb2);
}

/*Voor een mouse hover wordt de gradient omgedraaid*/
