/* --------------------------------

Primary style

-------------------------------- */
.navbar {
  width: 100vw;
}

.cd-user-avatar {
  width: 32px;
  height: 32px;
  border-radius: 4px;
  margin-right: 4px;
}

#cd-main-trigger, #cd-user-avatar {
  opacity: 1;
  transition: opacity .25s ease-in-out;
  -moz-transition: opacity .25s ease-in-out;
  -webkit-transition: opacity .25s ease-in-out;
}

#cd-main-trigger.fade {
  opacity: 0.2;
}

#cd-user-avatar.fade {
  opacity: 0.5;
}

.cd-main-header:hover #cd-main-trigger.fade, .cd-main-header:hover #cd-user-avatar.fade {
  opacity: 1;
}

/* --------------------------------

Main components

-------------------------------- */
.cd-main-content {
  margin-left: 200px;
  padding: 15px;
  position: relative;
  -webkit-transition: -webkit-transform 0.3s;
  -moz-transition: -moz-transform 0.3s;
  transition: transform 0.3s;
}

@media (max-width: 991.98px) {
  #menu.target ~ .cd-main-content,
  #menu:target ~ .cd-main-content {
    transform: translateX(-260px);
  }
}

.cd-main-content {
  min-height: 100vh;
}

.cd-main-header {
  z-index: 3;
  height: 50px;
}

.cd-main-header {
  /* add .nav-is-fixed class to body if you want a fixed navigation on > 960px */
  position: fixed;
  top: 0;
  right: 0;
}

.cd-nav {
  position: fixed;
  right: 0;
  z-index: 1050;
}

.cd-primary-nav, .cd-primary-nav ul {
  position: fixed;
  top: 0;
  right: 0;
  bottom: 0;
  margin-bottom: 0;
  width: 260px;
  background: #0a338d;
  overflow-y: auto;
  overflow-x: hidden;
  -webkit-overflow-scrolling: touch;
  /* Force Hardware Acceleration in WebKit */
  transform: translateZ(0);
  transition: transform 0.3s;
  will-change: transform;
}

.cd-primary-nav a, .cd-primary-nav ul a {
  display: block;
  height: 50px;
  line-height: 50px;
  padding: 0 20px;
  color: #fffff;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
  transform: translateZ(0);
  will-change: transform, opacity;
  transition: transform 0.3s, opacity 0.3s;
}

@media (max-width: 991.98px) {
  .cd-primary-nav a, .cd-primary-nav ul a {
    font-size: 1.4em;
  }
}

.cd-primary-nav.is-hidden, .cd-primary-nav ul.is-hidden {
  /* secondary navigations hidden by default */
  transform: translateX(100%);
}

.cd-primary-nav.moves-out > li > a, .cd-primary-nav ul.moves-out > li > a {
  /* push the navigation items to the left - and lower down opacity - when secondary nav slides in */
  transform: translateX(-100%);
  opacity: 0;
}

.cd-primary-nav .has-children > a::after {
  font-family: FontAwesome;
  content: "";
  right: 20px;
  position: absolute;
  margin-top: -1px;
  display: inline-block;
  height: 2px;
  width: 10px;
  -webkit-backface-visibility: hidden;
  backface-visibility: hidden;
}

.cd-primary-nav .has-children > a::after:hover {
  text-decoration: none;
}

.cd-primary-nav .go-back a::before {
  font-family: FontAwesome;
  content: "";
  right: 20px;
  position: absolute;
  margin-top: -1px;
  display: inline-block;
  height: 2px;
  width: 10px;
  -webkit-backface-visibility: hidden;
  backface-visibility: hidden;
}

.cd-primary-nav .go-back a::before:hover {
  text-decoration: none;
}

@media (max-width: 991.98px) {
  .nav-on-left .cd-primary-nav, .nav-on-left .cd-primary-nav ul {
    right: auto;
    left: 0;
  }
}

.cd-primary-nav .see-all a {
  /* different style for the See all button on mobile and tablet */
  color: #fffff;
}

@media (max-width: 991.98px) {
  .cd-primary-nav {
    /* by default .cd-primary-nav is hidden - trick for iOS devices where you can see the navigation if you pull down */
    visibility: hidden;
    transition: visibility 0s 0.3s, transform 0.3s;
    top: 42px;
    transform: translateX(260px);
  }
  .cd-primary-nav.nav-is-visible,
  #menu:target .cd-primary-nav,
  #menu.target .cd-primary-nav {
    visibility: visible;
    transition: visibility 0s 0s, transform 0.3s;
    transform: translateX(0);
  }
}

@media (min-width: 992px) {
  .navbar {
    display: none;
  }
  .cd-primary-nav {
    position: static;
    height: auto;
    width: auto;
    float: right;
    overflow: visible;
    background: none;
    transition: opacity .3s 0s, visibility 0s .3s;
  }
  .cd-primary-nav ul {
    position: static;
    height: auto;
    width: auto;
    background: transparent;
    overflow-x: hidden;
  }
  .cd-primary-nav ul.moves-out > li > a {
    /* reset mobile style */
    transform: translateX(0);
    opacity: 1;
  }
  .cd-primary-nav > li {
    float: left;
    margin-left: 3em;
  }
  .cd-primary-nav > li > a {
    /* main navigation buttons style */
    position: relative;
    display: inline-block;
    height: 50px;
    line-height: 50px;
    font-size: 1.4em;
    padding: 0 10px;
    color: #000000;
    overflow: visible;
    -webkit-transition: color 0.3s, box-shadow 0.3s;
    -moz-transition: color 0.3s, box-shadow 0.3s;
    transition: color 0.3s, box-shadow 0.3s;
  }
  .cd-primary-nav > li > a:hover, .cd-primary-nav > li > a.selected {
    color: #0a338d;
    box-shadow: inset 0 -2px 0 #0a338d;
    text-decoration: none;
  }
  .cd-primary-nav .go-back {
    display: none;
  }
  .cd-primary-nav .cd-secondary-nav {
    /* dropdown menu style */
    position: absolute;
    top: 50px;
    bottom: auto;
    width: 100vw;
    background: #ffffff;
    padding: 50px 0 100px 65px;
    box-shadow: inset 0 1px 0 #ccc, 0 3px 6px #ccc;
    transform: translateX(0);
    opacity: 0;
    visibility: hidden;
    transition: opacity .3s 0s, visibility 0s .3s;
  }
  .cd-primary-nav .cd-secondary-nav > li {
    /* change the height according to your needs - you can even set height: auto */
    height: 340px;
    /* here you set the number of columns - use width percentage */
    width: 17%;
    float: left;
    margin-right: 2.66%;
    border-right: 1px solid #e2e3df;
    overflow-x: hidden;
    overflow-y: auto;
    -webkit-overflow-scrolling: touch;
  }
  .cd-primary-nav .cd-secondary-nav > li > a, .cd-primary-nav .cd-secondary-nav > li > a:hover {
    /* secondary nav title */
    color: inherit;
    font-weight: bold;
    font-size: 1.6rem;
    margin-bottom: .6em;
    cursor: default;
  }
  .cd-primary-nav .cd-secondary-nav > li > a:hover, .cd-primary-nav .cd-secondary-nav > li > a:active {
    text-decoration: none;
  }
  .cd-primary-nav .cd-secondary-nav a {
    height: 30px;
    line-height: 30px;
    padding: 0 18% 0 0;
    color: #000000;
    font-size: 1.4rem;
  }
  .cd-primary-nav .cd-secondary-nav a:hover {
    color: #000000;
  }
  .cd-primary-nav .cd-secondary-nav ul {
    /* Force Hardware Acceleration in WebKit */
    transform: translateZ(0);
  }
  .cd-primary-nav .cd-secondary-nav ul ul {
    /* tertiary navigation */
    position: absolute;
    top: 0;
    left: 0;
    height: 100%;
    width: 100%;
  }
  .cd-primary-nav .cd-secondary-nav ul ul.is-hidden {
    transform: translateX(100%);
  }
  .cd-primary-nav .cd-secondary-nav ul ul .go-back {
    display: block;
  }
  .cd-primary-nav .cd-secondary-nav .moves-out > li > a {
    /* push the navigation items to the left - and lower down opacity - when tertiary nav slides in */
    transform: translateX(-100%);
  }
  .cd-secondary-nav.nav-is-visible,
  #menu:target .cd-secondary-nav,
  #menu.target .cd-secondary-nav {
    visibility: visible;
    opacity: 1;
    transition: opacity .3s 0s, visibility 0s 0s;
  }
  .cd-primary-nav .cd-secondary-nav > .has-children > a {
    /* Geen pijltje boven kolommen breed scherm */
    padding: 0;
  }
  .cd-primary-nav .cd-secondary-nav > .has-children > a::after {
    content: "";
  }
}

/* --------------------------------

support for no js

-------------------------------- */
.no-js .cd-primary-nav {
  position: relative;
  height: auto;
  width: 100%;
  overflow: visible;
  visibility: visible;
}

@media (min-width: 768px) {
  .no-js .cd-primary-nav {
