#cd-main-overlay {
  /* shadow layer visible when navigation is active */
  position: fixed;
  height: 100%;
  width: 100%;
  top: 0;
  left: 0;
  visibility: hidden;
  opacity: 0;
  backface-visibility: hidden;
  transition: opacity .3s 0s, visibility 0s .3s;
  z-index: 1040;
}

#cd-main-overlay.is-visible,
#menu.target ~ #cd-main-overlay,
#menu:target ~ #cd-main-overlay,
#search:target ~ #cd-main-overlay,
#search.target ~ #cd-main-overlay {
  /* Laat overlay zien als target is op menu of search */
  opacity: 1;
  visibility: visible;
  transition: opacity .3s 0s, visibility 0s 0s;
}

#search {
  position: absolute;
  top: 50px;
  right: 0;
  opacity: 0;
  visibility: hidden;
  background: rgba(255, 255, 255, 0.8);
  box-shadow: inset 0 1px 0 #ccc, 0 3px 6px #ccc;
  transition: opacity .3s 0s, visibility 0s .3s;
}

#search form {
  height: 100%;
  width: 100%;
}

#search.is-visible,
#search.target,
#search:target {
  z-index: 1050;
  opacity: 1;
  visibility: visible;
  -webkit-transition: opacity .3s 0s, visibility 0s 0s;
  -moz-transition: opacity .3s 0s, visibility 0s 0s;
  transition: opacity .3s 0s, visibility 0s 0s;
}

.nav-is-fixed #search {
