#wijzigstatus label {
  width: 153px;
  margin: 0;
  float: left;
}

#wijzigstatus .lidgegevens {
  margin: 10px 0 15px 13px;
  padding: 2px;
}

#wijzigstatus .novieten, #wijzigstatus #wijzigstatus .leden {
  margin: 0 0 5px 167px;
}

#statusForm input#field_postfix.FormElement {
  width: 100px;
}

.jarig {
