    padding-right: 0;
    padding-left: 0;
    border-left: 0;
    max-width: unset;
  }
}

.ProfielLogEntry {
  background: #f4f4f4;
  padding: 10px;
  margin: 5px 0;
}

.ProfielLogEntry .metadata {
  color: #555;
  margin-bottom: 5px;
}

.node {
  clear: left;
  border-left: 1px solid black;
}

.node .lid {
  padding: 4px 8px;
  text-align: left;
}

.node .kinderen {
  clear: left;
  margin-left: 25px;
}

/* Standaard Kleuren */
.overflow-x-hidden {
  overflow-x: hidden;
}

.overflow-hidden {
  overflow: hidden;
}

.form-group .twitter-typeahead {
  width: 100%;
}

.input-group {
  position: relative;
  display: flex;
  flex-wrap: wrap;
  align-items: stretch;
  width: 100%;
}

.input-group > .twitter-typeahead {
  position: relative;
  flex: 1 1 auto;
  width: 1%;
  margin-bottom: 0;
}

.input-group > .twitter-typeahead > .form-control,
.input-group > .twitter-typeahead > .custom-select,
.input-group > .twitter-typeahead > .custom-file {
  width: 100%;
}

.input-group > .twitter-typeahead > .form-control:focus,
.input-group > .twitter-typeahead > .custom-select:focus,
.input-group > .twitter-typeahead > .custom-file:focus {
  z-index: 3;
}

.input-group > .twitter-typeahead > .form-control:not(:last-child),
.input-group > .twitter-typeahead > .custom-select:not(:last-child) {
  border-top-right-radius: 0;
  border-bottom-right-radius: 0;
}

/* Login Form */
.login-form .InputField, .login-form .InputField:hover {
  border: none;
  width: 100px;
}

/* Courant Archief */
.CourantArchiefJaar {
  width: 111px;
  float: left;
}

/* Bijbelrooster */
.bijbelrooster {
  -webkit-columns: 250px auto;
  -moz-columns: 250px auto;
  columns: 250px auto;
  line-height: 2em;
}

/* Pasfotos */
img.pasfoto {
  float: left;
  width: 55px;
  height: 73px;
  margin: 1px 0 0 1px;
}

.lidLink img {
  transition: all .4s ease-out;
}

.lidLink:hover img {
  box-shadow: 0 0 5px #000;
  transform: scale(1.33);
}

/* lidinstellingen */
.Formulier label.instelling {
  clear: left;
  width: 50%;
}

div.vertical {
  transform: rotate(-90deg);
  filter: progid:DXImageTransform.Microsoft.BasicImage(rotation=3);
}

th {
  text-align: left;
}

td {
  vertical-align: text-bottom;
}

hr {
  height: 1px;
  background-color: black;
  border: 0 none;
}

ul.horizontal li {
  display: inline;
  padding-right: 20px;
}

ul.horizontal li.active {
  font-weight: bold;
}

ul.nobullets li {
  list-style-type: none;
}

.outer-shadow {
  box-shadow: 0 0 20px #000;
}

.breadcrumbs {
  display: inline-block;
  font-weight: bold;
}

.breadcrumbs select {
  vertical-align: initial;
}

.fade-content {
  opacity: 0;
  transition: opacity .3s .1s ease;
}

.fade-container:hover .fade-content {
  opacity: 1;
}

