/* Lustrum 2016*/
/** licht blauw: #d7e3e7 */
/** normaal blauw: #20648f */
/** donker blauw: #151b29 */
/** rode letters: #c52d30 */
/*Achtergrond kleur zijbalk, forum headers, navigatie menu*/
#zijbalk,
#forumtabel th,
.cd-primary-nav ul {
  background-color: #20648f !important;
}

/* Lijntje tussen ongelezen en gelezen berichten */
#forumtabel tr.ongelezenvanaf td {
  background-color: #c52d30 !important;
}

/*Koppen in rood*/
h1,
a,
a:hover,
.cd-primary-nav > li > a:hover,
.cd-primary-nav > li > a.selected {
  color: #c52d30;
}

/*NAVIGATIE MENU*/
/*Zorg voor rode borders*/
#zijbalk .blok .zijbalk-kopje {
  border: 0 solid #c52d30;
  border-bottom-width: 2px;
  margin-right: 40px;
  background-image: none;
}

/* letters moeten wit */
#zijbalk .blok .zijbalk-kopje a {
  color: #ffffff;
}

/*Veel tekst van de zijbalk in het wit*/
#zijbalk .ishetal .ja,
#zijbalk .ishetal .nee,
.staatrood,
#zijbalk .blok .item a,
#zijbalk .ishetal,
#zijbalk .blok .item {
  color: #ffffff;
}

/*Andere koppen ook in het wit*/
.cd-primary-nav .cd-secondary-nav a,
.cd-primary-nav .cd-secondary-nav > li > a:hover,
.cd-primary-nav .cd-secondary-nav > li > a {
  color: #ffffff;
}

/*Hovering over navigatiemenuknop zorgt voor onderstreeping*/
.cd-primary-nav .cd-secondary-nav a:hover {
  text-decoration: underline !important;
  color: #ffffff;
}

/*Streepje onder naam*/
.cd-primary-nav > li > a:hover, .cd-primary-nav > li > a.selected {
  box-shadow: inset 0 -2px 0 #ffffff;
}

/*Zijbalk grens van het scherm in donkerblauw*/
#zijbalk {
  border-color: #151b29;
}

/*Logo*/
#zijbalk .cd-beeldmerk {
  background-image: url("../../images/onontdekt_beeldmerk.png");
}

/*KNOPPEN*/
/*Knoppen in blauw gradient*/
.btn,
input[type=submit],
input[type=button],
.progress-bar-info,
.btn:focus,
input[type=submit]:focus,
input[type=button]:focus {
  background-image: linear-gradient(to bottom, #151b29, #2d7eb2);
}

/*Voor een mouse hover wordt de gradient omgedraaid*/
.btn:hover,
input[type=submit]:hover,
input[type=button]:hover {
  background-image: linear-gradient(to bottom, #2d7eb2, #151b29);
}

/*Actieve knoppen in rood gradient -> zo ontstaat verschil tussen hover en active*/
.btn.active {
  background-image: linear-gradient(to bottom, #c52d30, #f0262a);
}
